import React, { useState, useEffect } from 'react';
import { useTask } from '../context/TaskContext';
import { XMarkIcon, CalendarIcon, FlagIcon } from '@heroicons/react/24/outline';

const TaskForm = ({ task, onClose, onSave }) => {
  const [formData, setFormData] = useState({
    title: '',
    description: '',
    dueDate: '',
    dueTime: '',
    priority: 'Medium'
  });
  const [errors, setErrors] = useState({});
  const [loading, setLoading] = useState(false);
  
  const { createTask, updateTask } = useTask();

  useEffect(() => {
    if (task) {
      // Edit mode - populate form with task data
      const dueDateTime = task.dueDate ? new Date(task.dueDate) : null;
      setFormData({
        title: task.title || '',
        description: task.description || '',
        dueDate: dueDateTime ? dueDateTime.toISOString().split('T')[0] : '',
        dueTime: dueDateTime ? dueDateTime.toTimeString().slice(0, 5) : '',
        priority: task.priority || 'Medium'
      });
    }
  }, [task]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
    
    // Clear error for this field when user starts typing
    if (errors[name]) {
      setErrors(prev => ({ ...prev, [name]: '' }));
    }
  };

  const validateForm = () => {
    const newErrors = {};
    
    if (!formData.title.trim()) {
      newErrors.title = 'Task title is required';
    }
    
    if (!formData.description.trim()) {
      newErrors.description = 'Task description is required';
    }
    
    if (formData.dueDate && formData.dueTime) {
      const dueDateTime = new Date(`${formData.dueDate}T${formData.dueTime}`);
      if (dueDateTime < new Date()) {
        newErrors.dueDate = 'Due date cannot be in the past';
      }
    }
    
    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    if (!validateForm()) {
      return;
    }
    
    setLoading(true);
    
    try {
      const taskData = {
        ...formData,
        userId: task?.userId || 'current-user' // This would come from auth context
      };
      
      // Combine date and time if both are provided
      if (formData.dueDate && formData.dueTime) {
        taskData.dueDate = new Date(`${formData.dueDate}T${formData.dueTime}`).toISOString();
      } else if (formData.dueDate) {
        taskData.dueDate = new Date(formData.dueDate).toISOString();
      } else {
        delete taskData.dueDate;
      }
      
      delete taskData.dueTime; // Remove the separate time field
      
      let result;
      if (task) {
        result = updateTask(task.id, taskData);
      } else {
        result = createTask(taskData);
      }
      
      if (result.success) {
        onSave(result.task);
        onClose();
      } else {
        setErrors({ submit: result.error });
      }
    } catch (err) {
      setErrors({ submit: 'An unexpected error occurred. Please try again.' });
    } finally {
      setLoading(false);
    }
  };

  const priorityOptions = ['High', 'Medium', 'Low'];
  const priorityColors = {
    High: 'priority-high',
    Medium: 'priority-medium',
    Low: 'priority-low'
  };

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4">
      <div className="bg-white rounded-lg shadow-construction-lg max-w-md w-full max-h-[90vh] overflow-y-auto animate-slide-up">
        <div className="flex items-center justify-between p-6 border-b border-construction-gray-lighter">
          <h2 className="text-xl font-display font-bold text-construction-gray">
            {task ? 'Edit Task' : 'Create New Task'}
          </h2>
          <button
            onClick={onClose}
            className="text-construction-gray-light hover:text-construction-gray transition-colors duration-200"
          >
            <XMarkIcon className="h-6 w-6" />
          </button>
        </div>
        
        <form onSubmit={handleSubmit} className="p-6">
          {errors.submit && (
            <div className="mb-4 p-3 bg-construction-red bg-opacity-10 border border-construction-red rounded-lg">
              <p className="text-construction-red text-sm">{errors.submit}</p>
            </div>
          )}
          
          <div className="space-y-4">
            <div>
              <label htmlFor="title" className="block text-sm font-medium text-construction-gray mb-1">
                Task Title *
              </label>
              <input
                id="title"
                name="title"
                type="text"
                required
                className={`input-field ${errors.title ? 'border-construction-red' : ''}`}
                value={formData.title}
                onChange={handleChange}
                placeholder="Enter task title"
              />
              {errors.title && (
                <p className="mt-1 text-sm text-construction-red">{errors.title}</p>
              )}
            </div>
            
            <div>
              <label htmlFor="description" className="block text-sm font-medium text-construction-gray mb-1">
                Description *
              </label>
              <textarea
                id="description"
                name="description"
                rows={3}
                required
                className={`input-field ${errors.description ? 'border-construction-red' : ''}`}
                value={formData.description}
                onChange={handleChange}
                placeholder="Enter task description"
              />
              {errors.description && (
                <p className="mt-1 text-sm text-construction-red">{errors.description}</p>
              )}
            </div>
            
            <div className="grid grid-cols-2 gap-4">
              <div>
                <label htmlFor="dueDate" className="block text-sm font-medium text-construction-gray mb-1">
                  <CalendarIcon className="h-4 w-4 inline mr-1" />
                  Due Date
                </label>
                <input
                  id="dueDate"
                  name="dueDate"
                  type="date"
                  className={`input-field ${errors.dueDate ? 'border-construction-red' : ''}`}
                  value={formData.dueDate}
                  onChange={handleChange}
                  min={new Date().toISOString().split('T')[0]}
                />
                {errors.dueDate && (
                  <p className="mt-1 text-sm text-construction-red">{errors.dueDate}</p>
                )}
              </div>
              
              <div>
                <label htmlFor="dueTime" className="block text-sm font-medium text-construction-gray mb-1">
                  Due Time
                </label>
                <input
                  id="dueTime"
                  name="dueTime"
                  type="time"
                  className="input-field"
                  value={formData.dueTime}
                  onChange={handleChange}
                />
              </div>
            </div>
            
            <div>
              <label className="block text-sm font-medium text-construction-gray mb-2">
                <FlagIcon className="h-4 w-4 inline mr-1" />
                Priority Level
              </label>
              <div className="flex space-x-3">
                {priorityOptions.map(priority => (
                  <label
                    key={priority}
                    className="flex-1 cursor-pointer"
                  >
                    <input
                      type="radio"
                      name="priority"
                      value={priority}
                      checked={formData.priority === priority}
                      onChange={handleChange}
                      className="sr-only"
                    />
                    <div className={`text-center py-2 px-3 rounded-lg border-2 transition-all duration-200 ${
                      formData.priority === priority
                        ? `border-construction-blue ${priorityColors[priority]}`
                        : 'border-construction-gray-lighter hover:border-construction-gray-light'
                    }`}>
                      <span className={`text-sm font-medium ${
                        formData.priority === priority ? 'text-white' : 'text-construction-gray'
                      }`}>
                        {priority}
                      </span>
                    </div>
                  </label>
                ))}
              </div>
            </div>
          </div>
          
          <div className="mt-6 flex space-x-3">
            <button
              type="button"
              onClick={onClose}
              className="flex-1 btn-secondary"
              disabled={loading}
            >
              Cancel
            </button>
            <button
              type="submit"
              className="flex-1 btn-primary"
              disabled={loading}
            >
              {loading ? 'Saving...' : (task ? 'Update Task' : 'Create Task')}
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default TaskForm;
