import React, { useState, useEffect } from 'react';
import { useAuth } from '../context/AuthContext';
import { useTask } from '../context/TaskContext';
import TaskForm from '../components/TaskForm';
import {
  PlusCircleIcon,
  MagnifyingGlassIcon,
  FunnelIcon,
  ArrowUpDownIcon,
  CheckCircleIcon,
  ClockIcon,
  PencilIcon,
  TrashIcon,
  XMarkIcon,
  ClipboardDocumentListIcon
} from '@heroicons/react/24/outline';

const Tasks = () => {
  const { currentUser } = useAuth();
  const { getUserTasks, toggleTaskComplete, deleteTask, sortTasks, filterTasksByPriority } = useTask();
  
  const [tasks, setTasks] = useState([]);
  const [filteredTasks, setFilteredTasks] = useState([]);
  const [showTaskForm, setShowTaskForm] = useState(false);
  const [editingTask, setEditingTask] = useState(null);
  const [searchTerm, setSearchTerm] = useState('');
  const [sortBy, setSortBy] = useState('createdDate');
  const [filterPriority, setFilterPriority] = useState('all');
  const [showFilters, setShowFilters] = useState(false);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const userTasks = getUserTasks(currentUser?.id);
    setTasks(userTasks);
    setFilteredTasks(userTasks);
    setLoading(false);
  }, [currentUser, getUserTasks]);

  useEffect(() => {
    let result = [...tasks];
    
    // Apply search filter
    if (searchTerm) {
      const lowercaseSearch = searchTerm.toLowerCase();
      result = result.filter(task => 
        task.title.toLowerCase().includes(lowercaseSearch) ||
        task.description.toLowerCase().includes(lowercaseSearch)
      );
    }
    
    // Apply priority filter
    result = filterTasksByPriority(result, filterPriority);
    
    // Apply sorting
    result = sortTasks(result, sortBy);
    
    setFilteredTasks(result);
  }, [tasks, searchTerm, filterPriority, sortBy, sortTasks, filterTasksByPriority]);

  const handleCreateTask = () => {
    setEditingTask(null);
    setShowTaskForm(true);
  };

  const handleEditTask = (task) => {
    setEditingTask(task);
    setShowTaskForm(true);
  };

  const handleTaskFormSave = (savedTask) => {
    if (editingTask) {
      // Update existing task
      setTasks(prev => prev.map(t => t.id === savedTask.id ? savedTask : t));
    } else {
      // Add new task
      setTasks(prev => [savedTask, ...prev]);
    }
    setShowTaskForm(false);
    setEditingTask(null);
  };

  const handleToggleComplete = async (taskId) => {
    const result = toggleTaskComplete(taskId);
    if (result.success) {
      setTasks(prev => prev.map(t => t.id === taskId ? result.task : t));
    }
  };

  const handleDeleteTask = async (taskId) => {
    if (window.confirm('Are you sure you want to delete this task?')) {
      const result = deleteTask(taskId);
      if (result.success) {
        setTasks(prev => prev.filter(t => t.id !== taskId));
      }
    }
  };

  const getPriorityColor = (priority) => {
    switch (priority) {
      case 'High': return 'priority-high';
      case 'Medium': return 'priority-medium';
      case 'Low': return 'priority-low';
      default: return 'bg-gray-500 text-white';
    }
  };

  const TaskCard = ({ task }) => (
    <div className={`card p-4 ${task.completed ? 'opacity-75' : ''}`}>
      <div className="flex items-start justify-between mb-3">
        <div className="flex-1">
          <h3 className={`text-lg font-medium text-construction-gray ${task.completed ? 'task-complete' : ''}`}>
            {task.title}
          </h3>
          <p className={`text-sm text-construction-gray-light mt-1 ${task.completed ? 'task-complete' : ''}`}>
            {task.description}
          </p>
        </div>
        <span className={`badge ${getPriorityColor(task.priority)}`}>
          {task.priority}
        </span>
      </div>
      
      <div className="flex items-center justify-between">
        <div className="flex items-center text-sm text-construction-gray-light">
          {task.dueDate && (
            <>
              <ClockIcon className="h-4 w-4 mr-1" />
              <span>
                Due: {new Date(task.dueDate).toLocaleDateString()} 
                {new Date(task.dueDate).toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })}
              </span>
            </>
          )}
        </div>
        
        <div className="flex items-center space-x-2">
          <button
            onClick={() => handleToggleComplete(task.id)}
            className={`p-2 rounded-lg transition-colors duration-200 ${
              task.completed 
                ? 'bg-construction-green hover:bg-green-600 text-white' 
                : 'bg-construction-gray-lighter hover:bg-construction-gray-light text-construction-gray'
            }`}
            title={task.completed ? 'Mark as incomplete' : 'Mark as complete'}
          >
            <CheckCircleIcon className="h-4 w-4" />
          </button>
          
          <button
            onClick={() => handleEditTask(task)}
            className="p-2 bg-construction-blue hover:bg-construction-blue-light text-white rounded-lg transition-colors duration-200"
            title="Edit task"
          >
            <PencilIcon className="h-4 w-4" />
          </button>
          
          <button
            onClick={() => handleDeleteTask(task.id)}
            className="p-2 bg-construction-red hover:bg-red-600 text-white rounded-lg transition-colors duration-200"
            title="Delete task"
          >
            <TrashIcon className="h-4 w-4" />
          </button>
        </div>
      </div>
    </div>
  );

  if (loading) {
    return (
      <div className="flex items-center justify-center min-h-screen">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-construction-blue"></div>
      </div>
    );
  }

  return (
    <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
      <div className="flex flex-col sm:flex-row sm:items-center sm:justify-between mb-8">
        <h1 className="text-3xl font-display font-bold text-construction-gray">
          Construction Tasks
        </h1>
        <button
          onClick={handleCreateTask}
          className="mt-4 sm:mt-0 btn-primary flex items-center"
        >
          <PlusCircleIcon className="h-4 w-4 mr-2" />
          New Task
        </button>
      </div>

      {/* Search and Filters */}
      <div className="card mb-6">
        <div className="flex flex-col sm:flex-row gap-4">
          {/* Search */}
          <div className="flex-1">
            <div className="relative">
              <MagnifyingGlassIcon className="absolute left-3 top-1/2 transform -translate-y-1/2 h-5 w-5 text-construction-gray-light" />
              <input
                type="text"
                placeholder="Search tasks..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                className="input-field pl-10"
              />
            </div>
          </div>
          
          {/* Filter and Sort buttons */}
          <div className="flex space-x-2">
            <button
              onClick={() => setShowFilters(!showFilters)}
              className="btn-secondary flex items-center"
            >
              <FunnelIcon className="h-4 w-4 mr-2" />
              Filters
              {(filterPriority !== 'all' || searchTerm) && (
                <span className="ml-2 bg-construction-orange text-white text-xs px-2 py-1 rounded-full">
                  Active
                </span>
              )}
            </button>
            
            <select
              value={sortBy}
              onChange={(e) => setSortBy(e.target.value)}
              className="input-field flex items-center"
            >
              <option value="createdDate">Sort by Created</option>
              <option value="dueDate">Sort by Due Date</option>
              <option value="priority">Sort by Priority</option>
              <option value="completed">Sort by Status</option>
            </select>
          </div>
        </div>
        
        {/* Expanded Filters */}
        {showFilters && (
          <div className="mt-4 pt-4 border-t border-construction-gray-lighter">
            <div className="flex flex-wrap gap-3">
              <div className="flex items-center space-x-2">
                <span className="text-sm font-medium text-construction-gray">Priority:</span>
                {['all', 'High', 'Medium', 'Low'].map(priority => (
                  <button
                    key={priority}
                    onClick={() => setFilterPriority(priority)}
                    className={`px-3 py-1 rounded-full text-sm font-medium transition-colors duration-200 ${
                      filterPriority === priority
                        ? priority === 'all' 
                          ? 'bg-construction-gray text-white'
                          : getPriorityColor(priority)
                        : 'bg-construction-gray-lighter text-construction-gray hover:bg-construction-gray-light'
                    }`}
                  >
                    {priority === 'all' ? 'All' : priority}
                  </button>
                ))}
              </div>
              
              {(searchTerm || filterPriority !== 'all') && (
                <button
                  onClick={() => {
                    setSearchTerm('');
                    setFilterPriority('all');
                  }}
                  className="flex items-center text-sm text-construction-red hover:text-red-600"
                >
                  <XMarkIcon className="h-4 w-4 mr-1" />
                  Clear filters
                </button>
              )}
            </div>
          </div>
        )}
      </div>

      {/* Task List */}
      {filteredTasks.length > 0 ? (
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
          {filteredTasks.map(task => (
            <TaskCard key={task.id} task={task} />
          ))}
        </div>
      ) : (
        <div className="text-center py-12">
          <ClipboardDocumentListIcon className="h-12 w-12 text-construction-gray-light mx-auto mb-4" />
          <h3 className="text-lg font-medium text-construction-gray mb-2">
            {searchTerm || filterPriority !== 'all' ? 'No tasks found' : 'No tasks yet'}
          </h3>
          <p className="text-construction-gray-light mb-6">
            {searchTerm || filterPriority !== 'all' 
              ? 'Try adjusting your search or filters'
              : 'Create your first construction task to get started'
            }
          </p>
          {!searchTerm && filterPriority === 'all' && (
            <button
              onClick={handleCreateTask}
              className="btn-primary flex items-center mx-auto"
            >
              <PlusCircleIcon className="h-4 w-4 mr-2" />
              Create Task
            </button>
          )}
        </div>
      )}

      {/* Task Form Modal */}
      {showTaskForm && (
        <TaskForm
          task={editingTask}
          onClose={() => {
            setShowTaskForm(false);
            setEditingTask(null);
          }}
          onSave={handleTaskFormSave}
        />
      )}
    </div>
  );
};

export default Tasks;
