import React from 'react';
import { Link } from 'react-router-dom';
import { useTask } from '../../context/TaskContext';
import { useUser } from '../../context/UserContext';
import {
  UsersIcon,
  ClipboardDocumentListIcon,
  ClockIcon,
  ExclamationTriangleIcon,
  ChartBarIcon,
  BuildingOfficeIcon,
  ArrowTrendingUpIcon,
  ArrowTrendingDownIcon
} from '@heroicons/react/24/outline';

const AdminDashboard = () => {
  const { getTaskStats, getAllTasks } = useTask();
  const { getUserStats } = useUser();
  
  const globalTaskStats = getTaskStats();
  const allTasks = getAllTasks();
  const userStats = getUserStats();
  
  // Calculate additional metrics
  const activeUsers = userStats.filter(user => user.taskCount > 0).length;
  const totalUsers = userStats.length;
  const completionRate = globalTaskStats.total > 0 
    ? Math.round((globalTaskStats.completed / globalTaskStats.total) * 100)
    : 0;
  
  // Get recent activity
  const recentTasks = allTasks
    .sort((a, b) => new Date(b.createdAt) - new Date(a.createdAt))
    .slice(0, 5);
  
  // Get top users by task count
  const topUsers = [...userStats]
    .sort((a, b) => b.taskCount - a.taskCount)
    .slice(0, 5);

  const StatCard = ({ title, value, subtitle, icon: Icon, color, trend, trendValue }) => (
    <div className="card">
      <div className="flex items-center justify-between mb-4">
        <div className={`p-3 rounded-lg ${color}`}>
          <Icon className="h-6 w-6 text-white" />
        </div>
        {trend && (
          <div className={`flex items-center text-sm ${
            trend === 'up' ? 'text-construction-green' : 'text-construction-red'
          }`}>
            {trend === 'up' ? (
              <ArrowTrendingUpIcon className="h-4 w-4 mr-1" />
            ) : (
              <ArrowTrendingDownIcon className="h-4 w-4 mr-1" />
            )}
            {trendValue}
          </div>
        )}
      </div>
      <div>
        <p className="text-2xl font-bold text-construction-gray">{value}</p>
        <p className="text-sm text-construction-gray-light mt-1">{title}</p>
        {subtitle && (
          <p className="text-xs text-construction-gray-light mt-1">{subtitle}</p>
        )}
      </div>
    </div>
  );

  const TaskItem = ({ task, user }) => (
    <div className="flex items-center justify-between p-3 bg-gray-50 rounded-lg">
      <div className="flex-1">
        <h4 className={`text-sm font-medium text-construction-gray ${task.completed ? 'task-complete' : ''}`}>
          {task.title}
        </h4>
        <p className="text-xs text-construction-gray-light">
          by {user?.name || 'Unknown'} ‚Ä¢ {new Date(task.createdAt).toLocaleDateString()}
        </p>
      </div>
      <div className="flex items-center space-x-2">
        <span className={`badge priority-${task.priority.toLowerCase()}`}>
          {task.priority}
        </span>
        {task.completed && (
          <span className="badge bg-construction-green text-white">
            Done
          </span>
        )}
      </div>
    </div>
  );

  const UserItem = ({ user, index }) => (
    <div className="flex items-center justify-between p-3 bg-gray-50 rounded-lg">
      <div className="flex items-center">
        <div className="flex-shrink-0 w-8 h-8 bg-construction-blue rounded-full flex items-center justify-center">
          <span className="text-white text-sm font-medium">
            {index + 1}
          </span>
        </div>
        <div className="ml-3">
          <p className="text-sm font-medium text-construction-gray">
            {user.name}
          </p>
          <p className="text-xs text-construction-gray-light">
            {user.company || 'No company'}
          </p>
        </div>
      </div>
      <div className="text-right">
        <p className="text-sm font-medium text-construction-gray">
          {user.taskCount} tasks
        </p>
        <p className="text-xs text-construction-gray-light">
          {user.completedTasks} completed
        </p>
      </div>
    </div>
  );

  return (
    <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
      <div className="mb-8">
        <h1 className="text-3xl font-display font-bold text-construction-gray">
          Admin Dashboard
        </h1>
        <p className="mt-2 text-construction-gray-light">
          Overview of all construction tasks and user activity
        </p>
      </div>

      {/* Stats Grid */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6 mb-8">
        <StatCard
          title="Total Users"
          value={totalUsers}
          subtitle={`${activeUsers} active`}
          icon={UsersIcon}
          color="bg-construction-blue"
          trend="up"
          trendValue="+12%"
        />
        <StatCard
          title="Total Tasks"
          value={globalTaskStats.total}
          subtitle={`${globalTaskStats.completed} completed`}
          icon={ClipboardDocumentListIcon}
          color="bg-construction-orange"
          trend="up"
          trendValue="+8%"
        />
        <StatCard
          title="Completion Rate"
          value={`${completionRate}%`}
          subtitle={`${globalTaskStats.pending} pending`}
          icon={ChartBarIcon}
          color="bg-construction-green"
          trend="up"
          trendValue="+5%"
        />
        <StatCard
          title="High Priority"
          value={globalTaskStats.high}
          subtitle="Requires attention"
          icon={ExclamationTriangleIcon}
          color="bg-construction-red"
          trend={globalTaskStats.high > 5 ? 'down' : 'up'}
          trendValue={globalTaskStats.high > 5 ? '-2' : '+1'}
        />
      </div>

      <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">
        {/* Recent Tasks */}
        <div className="lg:col-span-2">
          <div className="card">
            <div className="flex items-center justify-between mb-6">
              <h2 className="text-xl font-display font-bold text-construction-gray flex items-center">
                <ClockIcon className="h-5 w-5 mr-2 text-construction-blue" />
                Recent Tasks
              </h2>
              <Link
                to="/admin/tasks"
                className="text-sm text-construction-blue hover:text-construction-blue-light font-medium"
              >
                View all
              </Link>
            </div>
            
            {recentTasks.length > 0 ? (
              <div className="space-y-3">
                {recentTasks.map(task => {
                  const user = userStats.find(u => u.id === task.userId);
                  return <TaskItem key={task.id} task={task} user={user} />;
                })}
              </div>
            ) : (
              <div className="text-center py-8">
                <ClipboardDocumentListIcon className="h-12 w-12 text-construction-gray-light mx-auto mb-4" />
                <p className="text-construction-gray-light">No tasks created yet</p>
              </div>
            )}
          </div>
        </div>

        {/* Sidebar */}
        <div className="space-y-6">
          {/* Top Performers */}
          <div className="card">
            <h3 className="text-lg font-display font-bold text-construction-gray mb-4">
              Top Performers
            </h3>
            {topUsers.length > 0 ? (
              <div className="space-y-3">
                {topUsers.map((user, index) => (
                  <UserItem key={user.id} user={user} index={index} />
                ))}
              </div>
            ) : (
              <p className="text-sm text-construction-gray-light">
                No users yet
              </p>
            )}
          </div>

          {/* Quick Actions */}
          <div className="card">
            <h3 className="text-lg font-display font-bold text-construction-gray mb-4">
              Quick Actions
            </h3>
            <div className="space-y-3">
              <Link
                to="/admin/users"
                className="w-full btn-primary flex items-center justify-center"
              >
                <UsersIcon className="h-4 w-4 mr-2" />
                Manage Users
              </Link>
              <Link
                to="/admin/tasks"
                className="w-full btn-secondary flex items-center justify-center"
              >
                <ClipboardDocumentListIcon className="h-4 w-4 mr-2" />
                View All Tasks
              </Link>
            </div>
          </div>

          {/* Company Info */}
          <div className="card bg-gradient-to-br from-construction-blue to-construction-blue-light text-white">
            <div className="flex items-center mb-3">
              <BuildingOfficeIcon className="h-8 w-8 mr-3 text-hard-hat-yellow" />
              <div>
                <h3 className="font-display font-bold">Hanover & Tyke</h3>
                <p className="text-xs text-construction-gray-lighter">Admin Panel</p>
              </div>
            </div>
            <div className="text-sm">
              <p className="mb-2">Managing construction projects with efficiency and professionalism.</p>
              <div className="inline-flex items-center px-2 py-1 rounded-full text-xs font-medium bg-hard-hat-yellow text-construction-blue">
                <span className="mr-1">üèóÔ∏è</span>
                Admin Access
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default AdminDashboard;
